export const initialState = {
	basket:[],
}

export const getTotalBasket=basket.reduce(function(prev, cur) {
  return prev + cur.price;
	}, 0);

const reducer =(state,action) =>{
	console.log(action);
	switch (action.type) {

	case 'ADD_TO_BASKET':
		return { ...state, basket:[...state.basket,action.item],
		 };

	default:
		return state;
	}
};

export default reducer;
